function dateInputs(){const e=document.querySelectorAll("[data-mask-input]");if(!e)return null;e.forEach((e=>{IMask(e,{mask:"+{7}(000)000-00-00"})}))}function consultationSlider(){if(!document.querySelector(".consultation"))return null;new Swiper(".consultation__slider",{slidesPerView:1,loop:!0,autoplay:{delay:3e3},navigation:{nextEl:".consultation__slider-arrow--next",prevEl:".consultation__slider-arrow--prev"}})}function benifitsSlider(){if(!document.querySelector(".benifits"))return null;new Swiper(".benifits__slider",{slidesPerView:1.5,navigation:{nextEl:".benifits__slider-arrow--next",prevEl:".benifits__slider-arrow--prev"},breakpoints:{480:{slidesPerView:2,spaceBetween:30},768:{slidesPerView:4,spaceBetween:30},800:{slidesPerView:6,spaceBetween:30},1025:{slidesPerView:7,spaceBetween:40}}})}dateInputs(),consultationSlider(),benifitsSlider();